import folium

# 여행지 정보 (예시)
travel_spots = [
    {"name": "Spot 1", "category": "Beach", "index": 80, "location": [37.5665, 126.9780], "country": "Korea"},
    {"name": "Spot 2", "category": "Mountain", "index": 60, "location": [37.7749, -122.4194], "country": "USA"},
    {"name": "Spot 3", "category": "Temple", "index": 90, "location": [35.6895, 139.6917], "country": "Japan"},
    # Add more travel spots as needed
]

# 기본 지도 생성
m = folium.Map(location=[37.5665, 126.9780], zoom_start=3)

# 국가에 따라 다른 이미지를 사용하여 마커 추가
for spot in travel_spots:
    icon_url = {
        "Korea": 'data:image/png;base64,',
        "USA": 'data:image/png;base64,',
        "Japan": 'data:image/png;base64,'
    }.get(spot['country'], 'https://example.com/default_icon.png')

    folium.Marker(
        location=spot["location"],
        popup=f"{spot['name']} - Category: {spot['category']}, Index: {spot['index']}",
        icon=folium.CustomIcon(icon_url, icon_size=(30, 30))
    ).add_to(m)

# 지도를 HTML 파일로 저장
m.save("maps/travel_map5.html")